css:
.center {
  display: block;
  margin-left: auto;
  margin-right: auto;
}
.bold{
    font-weight: bold;
}
#wrapper {
    width: 500px;
    overflow: hidden; /* will contain if #first is longer than #second */
    font-size:14px;
}
#first {
    width: 165px;
    float:left; /* add this */
    /*border: 1px solid red;*/
}
#second {
    /*border: 1px solid green;*/
    overflow: hidden; /* if you don't want #second to wrap below #first */
}
@media screen {
  div.divFooter {
    display: none;
  }
}
@media print {
  div.divFooter {
    position: fixed;
    bottom: 0;
  }
}
