css:
*{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    word-wrap: break-word;
    font-family: arial;
}
.wrapper{
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    width: 100%;
    height: 100%;
    background-image: url('/files/tnp-resume.png');
    background-size: cover;
    background-repeat: no-repeat;
    display:flex;
    flex-direction: column;
    align-items:center;
    padding: 0px 5%;
}
.resumeHead{
    width: 100%;
    height: 20%;
    padding: 0px 2%;
    display: flex;
    justify-content:space-between;
    align-items: center;
    /*background-color: green;*/
}
.pImage{
    height : 70%;
    /*background-color: red;*/
}
.intro{
    width : 60%;
    height: 80%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: flex-start;
    /*background-color: yellow;*/
}
.name{
    font-size : 28px;
    font-weight: bold;
    text-transform: uppercase;
}
.summary{
    width: 100%;
    height: 60%;
}
.resumeBody{
    width: 100%;
    height: 80%;
    display: flex;
    justify-content:space-between;
    align-items: flex-start;
    /*background-color: limegreen;*/
}
.leftSec{
    width: 25%;
    height: 100%;
    /*background-color: purple;*/
    padding: 0 2%;
}
.secHead{
    font-size: 18px;
    font-weight: bold;
}
.sideSec{
    padding : 10px 0px;
    display:flex;
    flex-direction:column;
    justify-content: space-between;
}
.contactSection{
    width: 100%;
    height: auto;
    border-top: 3px solid #199ab1;
    /*background-color: navy;*/
}
.conRec{
    display: flex;
    align-items: center;
    padding: 5px 0px;
}
.conLogo{
    height: 15px;
    padding-right: 5px;
}
.qualificationSection{
    width: 100%;
    height: auto;
    border-top: 3px solid #199ab1;
    /*background-color: royalblue;*/
}
.techSkills{
    height: auto;
    width: 100%;
}
.nontechSkills{
    height: auto;
    width: 100%;
}
.subSecHead{
    font-size: 14px;
    font-weight: bold;
    padding: 5px 0px;
}
ol{
    list-style-position: inside;
}
.interestSection{
    width: 100%;
    height: auto;
    border-top: 3px solid #199ab1;
    /*background-color: steelblue;*/
}
.languageSection{
    width: 100%;
    height: auto;
    border-top: 3px solid #199ab1;
    /*background-color: skyblue;*/
}
.rightSec{
    width: 65%;
    height: 100%;
    /*background: firebrick;*/
}
.experienceSection{
    width: 100%;
    height: auto;
    padding-bottom: 20px;
     border-top: 3px solid #199ab1;
    /*background: orange;*/
}
.expSecBody{
    width: 100%;
    height: auto;
}
.expRec{
    display: flex;
    flex-direction: column;
    padding-top: 20px;
    padding-left: 20px;
}
.duration{
    font-style: italic;
}
.role{
    font-size: 16px;
    font-style: italic;
    font-weight: bold;
}
.organization{
    font-weight:bold;
    font-size: 16px;
}
.location{
    font-size: 16px;
}
.worksummary{
    margin-top: 25px;
}
.educationSection{
    width: 100%;
    height: auto;
     border-top: 3px solid #199ab1;
    /*background: limegreen;*/
}
.eduSecBody{
    width: 100%;
    height: auto;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}
.eduRec{
    margin: 10px 0px;
}
.status_year, .score{
    font-size: 14px;
}
.course, .institute{
    font-size: 16px;
    font-weight: bolder;
}
.footer{
    width: 100%;
    height: 30%;
    /*background-color: pink;*/
}
.footerHead{
    margin-bottom: 10px;
}
.footerBody{
    width: 100%;
    height: 80%;
    /*background-color: purple;*/
    display: flex;
    flex-direction: column;
    justify-content:space-between;
}
.declaration{
    font-size: 16px;
}
.signoff{
    width: 100%;
    height: 80%;
    /*background: green;*/
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.datePlace, .signature{
    width: 40%;
    height: 100%;
    /*background: orange;*/

}
.datePlace{
    display: flex;
    flex-direction: column;
}
.date,.place{
    padding : 10px 0px;
}
.signature{
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
}
.sigImage{
    width: 80px;
}
.imgsec{
    width: 100%;
    height: auto;
    border: none;
    padding : 10px 0px;
    display:flex;
    flex-direction:column;
    justify-content: space-between;
}
.expsec{
    width: 100%;
    height: auto;
    padding-bottom: 20px;
    border: none;
}
