css:
body {
        justify-content: center;
        align-items: center;
        height: 100vh;
    }

    .container {
        text-align: center;
    }

    .centered-elements {
        width: 80%;
        margin: 2% auto;
        display: flex;
        flex-direction: row;
        align-items: center;
    }

    .centered-elements img {
        width: 20%;
        margin: 2%;
    }

    h1 {
        margin: 2% auto;
        font-size: 36px;
        font-family: ALGERIAN;
    }

    h2 {
        margin: 2% auto;
        font-size: 24px;
        font-family: ALGERIAN;
    }

    table {
        width: 90%;
        margin: 2% auto;
        border: 1px solid black;
        border-collapse: collapse;
    }

    .tr-first-last {
        padding: 1%;
        text-align: left;
        background-color: rgb(231, 226, 226);
        font-weight: bold;
    }

    tr {
        padding: 10px;
        text-align: left;
        font-weight: bold;
    }

    td {
        padding: 8px;
        text-align: left;
    }
