css:
.wrapper{
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    width: 100%;
    height: 100%;
    background-image: url('/files/Certificate No..png');
    background-size: cover;
    background-repeat: no-repeat;
    display:flex;
    justify-content:center;
    align-items:center;
}
.certId{
    position: absolute;
    top: 6.2%;
    left: 28%;
    font-size: 20px;
}
.generalInfo{
    width : 60%;
    height: 30%;
    display:flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
}
.textLine{
    font-family: Arial, Helvetica, sans-serif;
    font-size: 16px;
    padding: 5px 0px;
}
.name{
    color: #403676;
    font-size: 24px;
    font-family: Arial, Helvetica, sans-serif;
    font-weight: bold;
    padding: 5px 0px;
}
.programName, .course{
    font-size: 24px;
    padding: 5px 0px;
    font-weight: bold;
    font-family: Arial, Helvetica, sans-serif;
}
.datecontainer{
    padding: 5px 0px;
    font-family: Arial, Helvetica, sans-serif;
}
.date{
    font-weight: bold;
    font-size: 16px;
}
.signatureContainer{
    width: 80%;
    height: 18%;
    position:absolute;
    bottom: 15%;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    justify-content:space-between;
    align-items: center;
}
.sigset1, .sigset2{
    width: 40%;
    height: 100%;
    display:flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
}
.signature{
    height: 55%;
}
.signature img{
    height: 100%;
}
.signatoryDetails{
    width:100%;
    height: 45%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
}
.signatoryDetails div{
    font-size: 16px;
}
.signatory{
    font-weight: bold;
}
