css:
.wrapper{
    position: absolute;
    top: 0;
    left: 0;
    width: 96%;
    height: 98%;
    background-color: white;
    margin:2%;
}

.certContainer{
    position: relative;
    width: 100%;
    height: 100%;
    border: 4px solid #70ceda;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
}
.boxa, .boxb{
    position: absolute;
    width: 400px;
    height: 300px;
}
.boxa{
    z-index: 2;
    background-color: #75249a;
}
.boxb{
    z-index: 1;
    background-color: #80d2de;
}
.box1{
    top: -240px;
    right: -100px;
    rotate: 22deg;
}
.box2{
    top: -251px;
    right: -45px;
    rotate: 30deg;
}
.box3{
    bottom: -240px;
    left: -100px;
    rotate: 22deg;
}
.box4{
    bottom: -251px;
    left: -45px;
    rotate: 30deg;
}

header{
    width: 100%;
    height: 20%;
    /* background-color: orange; */
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-items: flex-start;
    padding: 1%;
}
.certNo{
    font-family: Arial, Helvetica, sans-serif;
    font-size: 20px;
    padding: 10px 0px;
}
.imageSet{
    width: 55%;
    height: 60%;
    /* background-color: royalblue; */
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.imageSet img{
    width: 28%;
    padding: 1%;
}
.vbar{
    height: 50%;
    width: 2px;
    background-color: rgb(168, 167, 167)
}
main{
    width: 100%;
    height: 50%;
}
.titleContainer{
    width: 100%;
    height: 25%;
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    align-items: center;
    /* background-color: violet; */
}
.title{
    font-family: Arial, Helvetica, sans-serif;
    font-size: 30px;
    font-weight: bold;
    color: #75249a;
    /* background-color: green; */
}
.arrowset{
    width: 70%;
    height: 10%;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.arrow{
    width: 0;
    height: 10px;
    border-radius: 8px;
}
.leftarrow{
    border-top: 5px solid transparent;
    border-left: 5px solid transparent;
    border-right: 250px solid #80d2de;
    border-bottom: 5px solid transparent;
}
.rightarrow{
    border-top: 5px solid transparent;
    border-left: 250px solid #80d2de;
    border-right: 5px solid transparent;
    border-bottom: 5px solid transparent;
}
.mainContent{
    width: 100%;
    height: 75%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    /* background-color: orange; */
}
.line{
    font-family: Arial, Helvetica, sans-serif;
    font-size: 16px;
    padding: 5px 0px;
}
.participantName{
    color: #403676;
    font-size: 22px;
    font-family: Arial, Helvetica, sans-serif;
    font-weight: bold;
    padding: 5px 0px;
}
.programName, .course{
    font-size: 22px;
    padding: 5px 0px;
    font-weight: bold;
    font-family: Arial, Helvetica, sans-serif;
}
.datecontainer{
    padding: 5px 0px;
    font-family: Arial, Helvetica, sans-serif;
}
.date{
    font-weight: bold;
    font-size: 16px;
}
.footer{
    width: 100%;
    height: 20%;
    display: flex;
    justify-content: space-around;
    align-items: center;
    background-color: orange;
}
.signatureContainer{
    height: 100%;
    width:40%;
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    align-items: center;
    font-family: Arial, Helvetica, sans-serif;
}
.signature{
    width: 40%;
}
.signatoryDetails{
    height: 40%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    font-size: 14px;
}
.signatoryName{
    font-size: 18px;
    font-weight: bold;
}
